mapper.select:
select merchant_grab_id merchantGrabId,order_no orderNo, merchant_id merchantId, receipt_id receiptId, receipt_type receiptType, name, status, del, remark, img, CREATE_TIME createTime, CREATE_USER createUser, UPDATE_TIME updateTime, UPDATE_USER updateUser,money,actual,finish_time finishTime from merchant_grab where del='N'
and name like concat('%',#{condition},'%')
and merchant_id=#{merchantId}
