mapper.select:
SELECT TABLE_NAME as tableName
,column_name AS columnName, data_type AS dataType, character_maximum_length AS characterMaximumLength, numeric_precision AS numericPrecision, numeric_scale AS numericScale, is_nullable AS isNullable, CASE WHEN extra = 'auto_increment' THEN 1 ELSE 0 END AS isAutoIncrement, column_default AS columnDefault, column_comment AS columnComment ,c.ORDINAL_POSITION as ordinalPosition ,c.COLUMN_KEY as columnKey ,c.table_schema as tableSchema
from information_schema.COLUMNS c where TABLE_SCHEMA=#{tableSchema}
and TABLE_NAME=#{tableName}
group by TABLE_NAME order by TABLE_NAME asc
order by ORDINAL_POSITION asc
